package MyApp;
use strict;
use warnings;
use myUtils::KozakUtils;
use v5.10.0;
use Wx;
use wxPerl::Constructors;
use base 'Wx::App';

#my $cdna = '';
#my $cds = '';

#myUtils::KozakUtils::get_kozak_info($cdna, $cds);


  

  
sub OnInit {
	my $self = shift;
  
	my $frame = Wx::Frame->new(
		undef,
		-1,
		'A wxPerl Application',
		&Wx::wxDefaultPosition,
		&Wx::wxDefaultSize,
		&Wx::wxMAXIMIZE_BOX | &Wx::wxCLOSE_BOX
	);
	my $button = wxPerl::Button->new($frame, 'Click Me');
	Wx::Event::EVT_BUTTON($button, -1, sub {
		my ($b, $evt) = @_;
		$b->SetLabel('Clicked');
		$b->Disable;
	});
	$frame->Show;
}

MyApp->new->MainLoop;
