use strict;
use warnings;
use myUtils::MatchPWM;


my $matchpwm = myUtils::MatchPWM->instance();
#my $seq = "";
#my $seq = "";
#my $seq = 'CCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCC';
#my $seq = '';
#my $seq = "";
my $seq_snp = "";
my $seq_ins = "";

my $hits = $matchpwm->myUtils::MatchPWM::get_kozak_matches($seq_snp);
my $n = scalar @{$hits->{START}};
print "$n resultados snp \n";
for (my $i = 0; $i < $n; $i++){
	print $hits->{START}->[$i] . "\t" . $hits->{END}->[$i] . "\t" . $hits->{WIDTH}->[$i] . "\t" . $hits->{SCORE}->[$i] . "\t" . $hits->{INIT_CODON_POS}->[$i] . "\n";
	my $init = $hits->{INIT_CODON_POS}->[$i];
	print substr($seq_snp, $init, 3) . "\n";
}

$hits = $matchpwm->myUtils::MatchPWM::get_kozak_matches($seq_ins);
$n = scalar @{$hits->{START}};
print "$n resultados ins \n";
for (my $i = 0; $i < $n; $i++){
	print $hits->{START}->[$i] . "\t" . $hits->{END}->[$i] . "\t" . $hits->{WIDTH}->[$i] . "\t" . $hits->{SCORE}->[$i] . "\t" . $hits->{INIT_CODON_POS}->[$i] . "\n";
	my $init = $hits->{INIT_CODON_POS}->[$i];
	print substr($seq_ins, $init, 3) . "\n";
}
